const TeamCard = ({text, link, img}) => {
  return (
    <a
      className='team-card-container'
      href={link}
      target='_blank'
      rel='noreferrer'>
      <div className='team-card'>
        <div
          className='team-card__img'
          dangerouslySetInnerHTML={{__html: img}}></div>
        <p className='team-card__text'>{text}</p>
      </div>
    </a>
  );
};

const TeamContainer = ({cards = []}) => (
  <div className='team-container'>
    {cards.map(({link, text, img}) => (
      <TeamCard
        key={text}
        text={text}
        link={link}
        img={img}
      />
    ))}
  </div>
);

export const Team = ({data}) => {
  return (
    <section className='mach-team-wrapper wrapper flex-column'>
      <SectionHeader
        title={teamConfig.title}
        isCentred
      />
      <TeamContainer cards={teamConfig.cards} />
    </section>
  );
};
